module.exports = function(sequelize, DataTypes) {
	//	Define properties
	var Task = sequelize.define('Task', {
			title: DataTypes.STRING,
			complete: DataTypes.BOOLEAN
		}, {
		  	//	Define associations
			associate: function(models) {
				Task.belongsTo(models.User, { onDelete: 'CASCADE'} );
			}
		});
 
	return Task;
};